mapper.select:
SELECT idx, userId, content, adminResponses, createdAt, updatedAt
FROM inquiry
WHERE userId = #{userId}

SELECT idx, userId, content, adminResponses, createdAt, updatedAt
FROM inquiry
WHERE idx    = #{idx}
  AND userId = #{userId}

mapper.update:
UPDATE inquiry
SET adminResponses = #{adminResponses},
    updatedAt      = #{updatedAt}
WHERE idx = #{idx}

mapper.delete:
DELETE FROM inquiry
WHERE idx    = #{idx}
  AND userId = #{userId}
